import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Button, Card, Badge, Accordion } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

const MovieDetailPage = () => {
  const { movieId } = useParams();
  const navigate = useNavigate();
  const user = useSelector(state => state.auth.user);
  const [movie, setMovie] = useState(null);
  const [theaters, setTheaters] = useState([]);
  const [selectedTheater, setSelectedTheater] = useState(null);
  const [shows, setShows] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMovieDetails();
    fetchTheaters();
  }, [movieId]);

  const fetchMovieDetails = async () => {
    try {
      // Mock movie data
      const mockMovies = {
        '1': {
          _id: '1',
          title: 'Inception',
          genre: 'Sci-Fi, Thriller',
          duration: '148 minutes',
          rating: 8.8,
          description: 'A skilled thief who steals corporate secrets through dream-sharing technology is given the inverse task of planting an idea into the mind of a C.E.O.',
          image: 'https://via.placeholder.com/400x600?text=Inception'
        },
        '2': {
          _id: '2',
          title: 'The Dark Knight',
          genre: 'Action, Crime, Drama',
          duration: '152 minutes',
          rating: 9.0,
          description: 'When the menace known as the Joker wreaks havoc and chaos on the people of Gotham, Batman must accept one of the greatest psychological tests.',
          image: 'https://via.placeholder.com/400x600?text=Dark+Knight'
        },
        '3': {
          _id: '3',
          title: 'Interstellar',
          genre: 'Adventure, Drama, Sci-Fi',
          duration: '169 minutes',
          rating: 8.6,
          description: 'A team of explorers travel through a wormhole in space in an attempt to ensure humanity\'s survival.',
          image: 'https://via.placeholder.com/400x600?text=Interstellar'
        }
      };

      setMovie(mockMovies[movieId]);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching movie:', error);
      setLoading(false);
    }
  };

  const fetchTheaters = async () => {
    try {
      // Mock theater data
      const mockTheaters = [
        {
          _id: 'theater_1',
          name: 'PVR Cinemas',
          city: 'Mumbai',
          address: '123 Main Street, Mumbai',
          screens: 5
        },
        {
          _id: 'theater_2',
          name: 'Inox',
          city: 'Mumbai',
          address: '456 Park Avenue, Mumbai',
          screens: 4
        },
        {
          _id: 'theater_3',
          name: 'IMAX',
          city: 'Delhi',
          address: '789 MG Road, Delhi',
          screens: 3
        }
      ];
      setTheaters(mockTheaters);
    } catch (error) {
      console.error('Error fetching theaters:', error);
    }
  };

  const handleTheaterSelect = (theaterId) => {
    setSelectedTheater(theaterId);
    fetchShowsByTheater(theaterId);
  };

  const fetchShowsByTheater = async (theaterId) => {
    try {
      // Mock shows data
      const mockShows = {
        'theater_1': [
          {
            _id: 'show_1',
            startTime: new Date(Date.now() + 2 * 60 * 60 * 1000),
            endTime: new Date(Date.now() + 4 * 60 * 60 * 1000),
            price: 200,
            availableSeats: 30,
            totalSeats: 48
          },
          {
            _id: 'show_2',
            startTime: new Date(Date.now() + 6 * 60 * 60 * 1000),
            endTime: new Date(Date.now() + 8 * 60 * 60 * 1000),
            price: 200,
            availableSeats: 42,
            totalSeats: 48
          },
          {
            _id: 'show_4',
            startTime: new Date(Date.now() + 10 * 60 * 60 * 1000),
            endTime: new Date(Date.now() + 12 * 60 * 60 * 1000),
            price: 150,
            availableSeats: 20,
            totalSeats: 48
          }
        ],
        'theater_2': [
          {
            _id: 'show_1',
            startTime: new Date(Date.now() + 2.5 * 60 * 60 * 1000),
            endTime: new Date(Date.now() + 4.5 * 60 * 60 * 1000),
            price: 220,
            availableSeats: 25,
            totalSeats: 48
          },
          {
            _id: 'show_3',
            startTime: new Date(Date.now() + 7 * 60 * 60 * 1000),
            endTime: new Date(Date.now() + 9 * 60 * 60 * 1000),
            price: 250,
            availableSeats: 55,
            totalSeats: 72
          }
        ],
        'theater_3': [
          {
            _id: 'show_2',
            startTime: new Date(Date.now() + 3 * 60 * 60 * 1000),
            endTime: new Date(Date.now() + 5 * 60 * 60 * 1000),
            price: 200,
            availableSeats: 40,
            totalSeats: 48
          }
        ]
      };

      setShows(mockShows[theaterId] || []);
    } catch (error) {
      console.error('Error fetching shows:', error);
    }
  };

  const handleSelectSeats = (showId) => {
    if (!user) {
      alert('Please login first');
      navigate('/login');
      return;
    }
    navigate(`/seat-selection/${showId}`);
  };

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  if (!movie) {
    return <div className="text-center mt-5">Movie not found</div>;
  }

  return (
    <Container className="mt-5">
      <Row>
        <Col md={4} className="text-center">
          <img src={movie.image} alt={movie.title} className="img-fluid rounded" style={{ maxHeight: '500px' }} />
        </Col>
        <Col md={8}>
          <h1>{movie.title}</h1>
          <p>
            <Badge bg="info">{movie.rating}/10</Badge>
          </p>
          <p>
            <strong>Genre:</strong> {movie.genre}
          </p>
          <p>
            <strong>Duration:</strong> {movie.duration}
          </p>
          <p>
            <strong>Description:</strong>
          </p>
          <p>{movie.description}</p>
        </Col>
      </Row>

      <Row className="mt-5">
        <h3>Select a Theater to Book Tickets</h3>
        <Accordion className="mt-3">
          {theaters.map((theater, index) => (
            <Accordion.Item eventKey={index.toString()} key={theater._id}>
              <Accordion.Header>
                <strong>{theater.name}</strong> - {theater.city} ({theater.screens} screens)
              </Accordion.Header>
              <Accordion.Body>
                <p className="text-muted">{theater.address}</p>
                
                <h5 className="mt-3 mb-3">Available Shows</h5>
                <Row>
                  {selectedTheater === theater._id && shows.length > 0 ? (
                    shows.map((show) => (
                      <Col md={6} key={show._id} className="mb-3">
                        <Card className="h-100">
                          <Card.Body>
                            <h6>{new Date(show.startTime).toLocaleTimeString('en-US', {
                              hour: '2-digit',
                              minute: '2-digit',
                              hour12: true
                            })}</h6>
                            <p className="text-muted">
                              {new Date(show.startTime).toLocaleDateString()}
                            </p>
                            <p>
                              <strong>Price:</strong> â‚¹{show.price}
                            </p>
                            <p>
                              <strong>Available Seats:</strong> {show.availableSeats}/{show.totalSeats}
                            </p>
                            <Button
                              variant="success"
                              size="sm"
                              onClick={() => handleSelectSeats(show._id)}
                              disabled={show.availableSeats === 0}
                            >
                              {show.availableSeats > 0 ? 'Select Seats' : 'Show Full'}
                            </Button>
                          </Card.Body>
                        </Card>
                      </Col>
                    ))
                  ) : selectedTheater === theater._id && shows.length === 0 ? (
                    <Col md={12}>
                      <p className="text-muted">No shows available for this theater</p>
                    </Col>
                  ) : (
                    <Col md={12}>
                      <Button
                        variant="primary"
                        onClick={() => handleTheaterSelect(theater._id)}
                      >
                        View Shows
                      </Button>
                    </Col>
                  )}
                </Row>
              </Accordion.Body>
            </Accordion.Item>
          ))}
        </Accordion>
      </Row>
    </Container>
  );
};

export default MovieDetailPage;
