import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Spinner, Form } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { movieService, theaterService } from '../services/services';

const MovieDetailPage = () => {
  const { movieId } = useParams();
  const [movie, setMovie] = useState(null);
  const [theaters, setTheaters] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMovieDetails();
    fetchTheaters();
  }, [movieId]);

  const fetchMovieDetails = async () => {
    try {
      const response = await movieService.getMovieById(movieId);
      setMovie(response.data.data);
    } catch (error) {
      console.error('Error fetching movie details:', error);
    }
  };

  const fetchTheaters = async () => {
    try {
      const response = await theaterService.getAllTheaters();
      setTheaters(response.data.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching theaters:', error);
      setLoading(false);
    }
  };

  const filteredTheaters = selectedCity
    ? theaters.filter((t) => t.city === selectedCity)
    : theaters;

  const cities = [...new Set(theaters.map((t) => t.city))];

  if (loading || !movie) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <Row>
        <Col md={4}>
          <Card>
            <Card.Img variant="top" src={movie.posterUrl} alt={movie.title} />
            <Card.Body>
              <Card.Title>{movie.title}</Card.Title>
              <Card.Text>
                <strong>Duration:</strong> {movie.duration} min
              </Card.Text>
              <Card.Text>
                <strong>Genre:</strong> {movie.genre.join(', ')}
              </Card.Text>
              <Card.Text>
                <strong>Language:</strong> {movie.language.join(', ')}
              </Card.Text>
              <Card.Text>
                <strong>Rating:</strong> {movie.rating}/5
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
        <Col md={8}>
          <h2>{movie.title}</h2>
          <p>{movie.description}</p>
          
          <h4 className="mt-4">Select Theater</h4>
          <Form.Select
            value={selectedCity}
            onChange={(e) => setSelectedCity(e.target.value)}
            className="mb-3"
          >
            <option value="">All Cities</option>
            {cities.map((city) => (
              <option key={city} value={city}>{city}</option>
            ))}
          </Form.Select>

          <Row>
            {filteredTheaters.map((theater) => (
              <Col key={theater._id} md={6} className="mb-3">
                <Card>
                  <Card.Body>
                    <Card.Title>{theater.name}</Card.Title>
                    <Card.Text>{theater.location.address}</Card.Text>
                    <Button variant="primary" className="w-100">
                      Select Seats
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default MovieDetailPage;
