import React from 'react';
import { Container, Table, Button, Alert } from 'react-bootstrap';
import { useEffect, useState } from 'react';
import { bookingService } from '../services/services';
import { useSelector } from 'react-redux';

const MyBookingsPage = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const user = useSelector((state) => state.auth.user);

  useEffect(() => {
    if (user) {
      fetchBookings();
    }
  }, [user]);

  const fetchBookings = async () => {
    try {
      const response = await bookingService.getUserBookings(user.id);
      setBookings(response.data.data);
    } catch (error) {
      console.error('Error fetching bookings:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCancelBooking = async (bookingId) => {
    try {
      await bookingService.cancelBooking(bookingId);
      fetchBookings();
      alert('Booking cancelled successfully');
    } catch (error) {
      alert('Error cancelling booking');
    }
  };

  if (!user) {
    return (
      <Container className="mt-5">
        <Alert variant="warning">Please login to view your bookings</Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <h2 className="mb-4">My Bookings</h2>
      {bookings.length === 0 ? (
        <Alert variant="info">No bookings found</Alert>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Movie</th>
              <th>Seats</th>
              <th>Amount</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((booking) => (
              <tr key={booking._id}>
                <td>{booking.movieId.title}</td>
                <td>{booking.seats.join(', ')}</td>
                <td>â‚¹{booking.totalAmount}</td>
                <td>{booking.status}</td>
                <td>
                  {booking.status === 'confirmed' && (
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={() => handleCancelBooking(booking._id)}
                    >
                      Cancel
                    </Button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </Container>
  );
};

export default MyBookingsPage;
