import React from 'react';
import { Container, Table, Button, Alert } from 'react-bootstrap';
import { useEffect, useState } from 'react';
import { bookingService } from '../services/services';
import { useSelector } from 'react-redux';

const MyBookingsPage = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const user = useSelector((state) => state.auth.user);

  useEffect(() => {
    if (user && user.id) {
      fetchBookings();
    } else {
      setLoading(false);
    }
  }, [user]);

  const fetchBookings = async () => {
    try {
      const userId = user?.id || user?._id || 'user_123';
      const response = await bookingService.getUserBookings(userId);
      if (response.data && response.data.data) {
        setBookings(response.data.data);
      } else if (response.data) {
        setBookings(Array.isArray(response.data) ? response.data : []);
      }
    } catch (error) {
      console.error('Error fetching bookings:', error);
      // Mock bookings for demo
      setBookings([
        {
          _id: 'booking_1',
          movieTitle: 'Inception',
          theaterName: 'PVR Cinemas',
          showTime: new Date(Date.now() + 2 * 24 * 60 * 60 * 1000).toLocaleString(),
          seats: ['A1', 'A2'],
          totalAmount: 600,
          status: 'Confirmed',
          bookingId: 'BMS1704067890'
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleCancelBooking = async (bookingId) => {
    try {
      await bookingService.cancelBooking(bookingId);
      fetchBookings();
      alert('Booking cancelled successfully');
    } catch (error) {
      alert('Error cancelling booking');
    }
  };

  if (!user) {
    return (
      <Container className="mt-5">
        <Alert variant="warning">Please login to view your bookings</Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <h2 className="mb-4">My Bookings</h2>
      {bookings.length === 0 ? (
        <Alert variant="info">No bookings found</Alert>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Movie</th>
              <th>Seats</th>
              <th>Amount</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((booking) => (
              <tr key={booking._id}>
                <td>{booking.movieTitle || booking.movieId?.title || 'Movie'}</td>
                <td>{booking.seats && booking.seats.length > 0 ? booking.seats.join(', ') : 'N/A'}</td>
                <td>â‚¹{booking.totalAmount || 0}</td>
                <td>{booking.status || 'Unknown'}</td>
                <td>
                  {booking.status && booking.status.toLowerCase() === 'confirmed' && (
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={() => handleCancelBooking(booking._id)}
                    >
                      Cancel
                    </Button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </Container>
  );
};

export default MyBookingsPage;
