import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Spinner } from 'react-bootstrap';
import { movieService } from '../services/services';
import { useDispatch, useSelector } from 'react-redux';
import { setSelectedMovie } from '../redux/movieSlice';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    try {
      const response = await movieService.getAllMovies();
      setMovies(response.data.data);
    } catch (error) {
      console.error('Error fetching movies:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSelectMovie = (movie) => {
    dispatch(setSelectedMovie(movie));
    navigate(`/movie/${movie._id}`);
  };

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <h2 className="mb-4">Now Showing</h2>
      <Row>
        {movies.map((movie) => (
          <Col key={movie._id} md={3} className="mb-4">
            <Card>
              <Card.Img variant="top" src={movie.posterUrl} alt={movie.title} />
              <Card.Body>
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text>
                  <small>{movie.genre.join(', ')}</small>
                </Card.Text>
                <Card.Text>
                  <strong>Rating: {movie.rating}/5</strong>
                </Card.Text>
                <Button
                  variant="primary"
                  onClick={() => handleSelectMovie(movie)}
                  className="w-100"
                >
                  Book Tickets
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default HomePage;
